// based on https://github.com/montagejs/collections
/* global Map,Set */

import ShimmedMap from './Map';
import ShimmedSet from './Set';

var ExportedSet;
var ExportedMap;

if (process.env.COVERAGE) {
  // don't get penalized on coverage because these are unused
  ExportedSet = ShimmedSet;
  ExportedMap = ShimmedMap;
} else {
  // prefer built-in Map/Set
  ExportedSet = typeof Set === 'function' ? Set : ShimmedSet;
  ExportedMap = typeof Map === 'function' ? Map : ShimmedMap;
}

export {
  ExportedSet as Set,
  ExportedMap as Map
};
